"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[3479],{95748:(n,e,t)=>{t.r(e),t.d(e,{assets:()=>c,contentTitle:()=>a,default:()=>u,frontMatter:()=>o,metadata:()=>r,toc:()=>l});const r=JSON.parse('{"id":"\u53e4\u8bd7\u8bcd\u5e93/friendship","title":"friendship","description":"\u6545\u4eba\u897f\u8f9e\u9ec4\u9e64\u697c\uff0c\u70df\u82b1\u4e09\u6708\u4e0b\u626c\u5dde\u3002\u5b64\u5e06\u8fdc\u5f71\u78a7\u7a7a\u5c3d\uff0c\u552f\u89c1\u957f\u6c5f\u5929\u9645\u6d41\u3002","source":"@site/learn/\u53e4\u8bd7\u8bcd\u5e93/friendship.md","sourceDirName":"\u53e4\u8bd7\u8bcd\u5e93","slug":"/\u53e4\u8bd7\u8bcd\u5e93/friendship","permalink":"/learn/\u53e4\u8bd7\u8bcd\u5e93/friendship","draft":false,"unlisted":false,"editUrl":"https://github.com/teabagging/security/edit/main/website/learn/\u53e4\u8bd7\u8bcd\u5e93/friendship.md","tags":[],"version":"current","frontMatter":{},"sidebar":"devDocsSidebar","previous":{"title":"flower","permalink":"/learn/\u53e4\u8bd7\u8bcd\u5e93/flower"},"next":{"title":"frontier","permalink":"/learn/\u53e4\u8bd7\u8bcd\u5e93/frontier"}}');var i=t(74848),s=t(28453);const o={},a=void 0,c={},l=[];function p(n){const e={p:"p",...(0,s.R)(),...n.components};return(0,i.jsx)(e.p,{children:"\u6545\u4eba\u897f\u8f9e\u9ec4\u9e64\u697c\uff0c\u70df\u82b1\u4e09\u6708\u4e0b\u626c\u5dde\u3002\u5b64\u5e06\u8fdc\u5f71\u78a7\u7a7a\u5c3d\uff0c\u552f\u89c1\u957f\u6c5f\u5929\u9645\u6d41\u3002\n\u6bd5\u7adf\u897f\u6e56\u516d\u6708\u4e2d\uff0c\u98ce\u5149\u4e0d\u4e0e\u56db\u65f6\u540c\u3002\u63a5\u5929\u83b2\u53f6\u65e0\u7a77\u78a7\uff0c\u6620\u65e5\u8377\u82b1\u522b\u6837\u7ea2\u3002\n\u6e2d\u57ce\u671d\u96e8\u6d65\u8f7b\u5c18\uff0c\u5ba2\u820d\u9752\u9752\u67f3\u8272\u65b0\u3002(\u4e00\u4f5c\uff1a\u5ba2\u820d\u4f9d\u4f9d\u6768\u67f3\u6625)\u529d\u541b\u66f4\u5c3d\u4e00\u676f\u9152\uff0c\u897f\u51fa\u9633\u5173\u65e0\u6545\u4eba\u3002\n\u9752\u5c71\u6a2a\u5317\u90ed\uff0c\u767d\u6c34\u7ed5\u4e1c\u57ce\u3002\u6b64\u5730\u4e00\u4e3a\u522b\uff0c\u5b64\u84ec\u4e07\u91cc\u5f81\u3002 \u6d6e\u4e91\u6e38\u5b50\u610f\uff0c\u843d\u65e5\u6545\u4eba\u60c5\u3002\u6325\u624b\u81ea\u5179\u53bb\uff0c\u8427\u8427\u73ed\u9a6c\u9e23\u3002\n\u57ce\u9619\u8f85\u4e09\u79e6\uff0c\u98ce\u70df\u671b\u4e94\u6d25\u3002\u4e0e\u541b\u79bb\u522b\u610f\uff0c\u540c\u662f\u5ba6\u6e38\u4eba\u3002 \u6d77\u5185\u5b58\u77e5\u5df1\uff0c\u5929\u6daf\u82e5\u6bd4\u90bb\u3002\u65e0\u4e3a\u5728\u5c90\u8def\uff0c\u513f\u5973\u5171\u6cbe\u5dfe\u3002\n\u5341\u91cc\u9ec4\u4e91\u767d\u65e5\u66db\uff0c\u5317\u98ce\u5439\u96c1\u96ea\u7eb7\u7eb7\u3002 \u83ab\u6101\u524d\u8def\u65e0\u77e5\u5df1\uff0c\u5929\u4e0b\u8c01\u4eba\u4e0d\u8bc6\u541b\u3002 \u516d\u7fee\u98d8\u98d6\u79c1\u81ea\u601c\uff0c\u4e00\u79bb\u4eac\u6d1b\u5341\u9980\u5e74\u3002 \u4e08\u592b\u8d2b\u8df5\u5e94\u672a\u8db3\uff0c\u4eca\u65e5\u76f8\u9022\u65e0\u9152\u94b1\u3002\n\u767d\u6e38\u6cfe\u53bf\u6843\u82b1\u6f6d\uff0c\u6751\u4eba\u6c6a\u4f26\u5e38\u915d\u7f8e\u9152\u4ee5\u5f85\u767d\u674e\u767d\u4e58\u821f\u5c06\u6b32\u884c\uff0c\u5ffd\u95fb\u5cb8\u4e0a\u8e0f\u6b4c\u58f0\u3002\u6843\u82b1\u6f6d\u6c34\u6df1\u5343\u5c3a\uff0c\u4e0d\u53ca\u6c6a\u4f26\u9001\u6211\u60c5\u3002\xa0\n\u5bd2\u96e8\u8fde\u6c5f\u591c\u5165\u5434\uff0c\u5e73\u660e\u9001\u5ba2\u695a\u5c71\u5b64\u3002\u6d1b\u9633\u4eb2\u53cb\u5982\u76f8\u95ee\uff0c\u4e00\u7247\u51b0\u5fc3\u5728\u7389\u58f6\u3002\n\u5143\u548c\u5341\u5e74\uff0c\u4e88\u5de6\u8fc1\u4e5d\u6c5f\u90e1\u53f8\u9a6c\u3002\u660e\u5e74\u79cb\uff0c\u9001\u5ba2\u6e53\u6d66\u53e3\uff0c\u95fb\u821f\u4e2d\u591c\u5f39\u7435\u7436\u8005\u3002\u542c\u5176\u97f3\uff0c\u94ee\u94ee\u7136\u6709\u4eac\u90fd\u58f0\u3002\u95ee\u5176\u4eba\uff0c\u672c\u957f\u5b89\u5021\u5973\uff0c\u5c1d\u5b66\u7435\u7436\u4e8e\u7a46\u3001\u66f9\u4e8c\u5584\u624d\u3002\u5e74\u957f\u8272\u8870\uff0c\u59d4\u8eab\u4e3a\u8d3e\u4eba\u5987\u3002\u9042\u547d\u9152\uff0c\u4f7f\u5feb\u5f39\u6570\u66f2\u3002\u66f2\u7f62\u60af\u9ed8\uff0c\u81ea\u53d9\u5c11\u5c0f\u65f6\u6b22\u4e50\n\u601d\u4e61\u4e4b\u5ba2\u7a7a\u51dd\u56ac\uff0c\u5929\u8fb9\u6b32\u5c3d\u672a\u5c3d\u6625\u3002\u72ec\u6500\u6c5f\u6811\u6df1\u4e0d\u8bed\uff0c\u82b3\u8349\u843d\u82b1\u6101\u6740\u4eba\u3002\n\u6768\u67f3\u9752\u9752\u8457\u5730\u5782\uff0c\u6768\u82b1\u6f2b\u6f2b\u6405\u5929\u98de\u3002\u67f3\u6761\u6298\u5c3d\u82b1\u98de\u5c3d\uff0c\u501f\u95ee\u884c\u4eba\u5f52\u4e0d\u5f52\uff1f\n\u4e3a\u6211\u5f15\u676f\u6dfb\u9152\u996e\uff0c\u4e0e\u541b\u628a\u7bb8\u51fb\u76d8\u6b4c\u3002\u8bd7\u79f0\u56fd\u624b\u5f92\u4e3a\u5c14\uff0c\u547d\u538b\u4eba\u5934\u4e0d\u5948\u4f55\u3002\u4e3e\u773c\u98ce\u5149\u957f\u5bc2\u5bde\uff0c\u6ee1\u671d\u5b98\u804c\u72ec\u8e49\u8dce\u3002\u4ea6\u77e5\u5408\u88ab\u624d\u540d\u6298\uff0c\u4e8c\u5341\u4e09\u5e74\u6298\u592a\u591a\u3002\n\u843d\u53f6\u805a\u8fd8\u6563\uff0c\u5f81\u79bd\u53bb\u4e0d\u5f52\u3002\u4ee5\u6211\u7a77\u9014\u6ce3\uff0c\u6cbe\u541b\u51fa\u585e\u8863\u3002\n\u7ffb\u624b\u4f5c\u4e91\u8986\u624b\u96e8\uff0c\u7eb7\u7eb7\u8f7b\u8584\u4f55\u987b\u6570\u3002\u541b\u4e0d\u89c1\u7ba1\u9c8d\u8d2b\u65f6\u4ea4\uff0c\u6b64\u9053\u4eca\u4eba\u5f03\u5982\u571f\u3002\n\u5cb8\u96e8\u8fc7\u57ce\u5934\uff0c\u9ec4\u9e42\u4e0a\u620d\u697c\u3002\u585e\u82b1\u98d8\u5ba2\u6cea\uff0c\u8fb9\u67f3\u6302\u4e61\u6101\u3002\u767d\u53d1\u60b2\u660e\u955c\uff0c\u9752\u6625\u6362\u655d\u88d8\u3002\u541b\u4ece\u4e07\u91cc\u4f7f\uff0c\u95fb\u5df2\u5230\u74dc\u5dde\u3002\n\u4e07\u91cc\u8f9e\u5bb6\u4e8b\u9f13\u9f19\uff0c\u91d1\u9675\u9a7f\u8def\u695a\u4e91\u897f\u3002 \u6c5f\u6625\u4e0d\u80af\u7559\u5f52\u5ba2\uff0c\u8349\u8272\u9752\u9752\u9001\u9a6c\u8e44\u3002\n\u91cd\u95e8\u671d\u5df2\u542f\uff0c\u8d77\u5750\u542c\u8f66\u58f0\u3002\u8981\u6b32\u95fb\u6e05\u4f69\uff0c\u65b9\u5c06\u51fa\u6237\u8fce\u3002\u665a\u949f\u9e23\u4e0a\u82d1\uff0c\u758f\u96e8\u8fc7\u6625\u57ce\u3002\u4e86\u81ea\u4e0d\u76f8\u987e\uff0c\u4e34\u5802\u7a7a\u590d\u60c5\u3002\n\u8352\u6751\u5e26\u8fd4\u7167\uff0c\u843d\u53f6\u4e71\u7eb7\u7eb7\u3002\u53e4\u8def\u65e0\u884c\u5ba2\uff0c\u5bd2\u5c71\u72ec\u89c1\u541b\u3002 \u91ce\u6865\u7ecf\u96e8\u65ad\uff0c\u6da7\u6c34\u5411\u7530\u5206\u3002\u4e0d\u4e3a\u601c\u540c\u75c5\uff0c\u4f55\u4eba\u5230\u767d\u4e91\u3002\n\u695a\u6c5f\u5fae\u96e8\u91cc\uff0c\u5efa\u4e1a\u66ae\u949f\u65f6\u3002\u6f20\u6f20\u5e06\u6765\u91cd\uff0c\u51a5\u51a5\u9e1f\u53bb\u8fdf\u3002 \u6d77\u95e8\u6df1\u4e0d\u89c1\uff0c\u6d66\u6811\u8fdc\u542b\u6ecb\u3002\u76f8\u9001\u60c5\u65e0\u9650\uff0c\u6cbe\u895f\u6bd4\u6563\u4e1d\u3002\n\u4e16\u4eba\u7ed3\u4ea4\u987b\u9ec4\u91d1\uff0c\u9ec4\u91d1\u4e0d\u591a\u4ea4\u4e0d\u6df1\u3002 \u7eb5\u4ee4\u7136\u8bfa\u6682\u76f8\u8bb8\uff0c\u7ec8\u662f\u60a0\u60a0\u884c\u8def\u5fc3\u3002\n\u5723\u4ee3\u65e0\u9690\u8005\uff0c\u82f1\u7075\u5c3d\u6765\u5f52\u3002\u9042\u4ee4\u4e1c\u5c71\u5ba2\uff0c\u4e0d\u5f97\u987e\u91c7\u8587\u3002 \u65e2\u81f3\u541b\u95e8\u8fdc\uff0c\u5b70\u4e91\u543e\u9053\u975e\u3002\u6c5f\u6dee\u5ea6\u5bd2\u98df\uff0c\u4eac\u6d1b\u7f1d\u6625\u8863\u3002 \u7f6e\u9152\u4e34\u957f\u9053\uff0c\u540c\u5fc3\u4e0e\u6211\u8fdd\u3002\u884c\u5f53\u6d6e\u6842\u68f9\uff0c\u672a\u51e0\u62c2\u8346\u6249\u3002 \u8fdc\u6811\u5e26\u884c\u5ba2\uff0c\u5b64\u6751\u5f53\u843d\u6656\u3002\u543e\u8c0b\u9002\u4e0d\u7528\uff0c\u52ff\u8c13\u77e5\u97f3\u7a00\u3002\n\u4e07\u58d1\u6811\u53c2\u5929\uff0c\u5343\u5c71\u54cd\u675c\u9e43\u3002\u5c71\u4e2d\u4e00\u591c\u96e8\uff0c\u6811\u676a\u767e\u91cd\u6cc9\u3002 \u6c49\u5973\u8f93\u6a66\u5e03\uff0c\u5df4\u4eba\u8bbc\u828b\u7530\u3002\u6587\u7fc1\u7ffb\u6559\u6388\uff0c\u4e0d\u6562\u4f9d\u5148\u8d24\u3002\n\u7ed3\u53d1\u672a\u8bc6\u4e8b\uff0c\u6240\u4ea4\u5c3d\u8c6a\u96c4\u3002\u5374\u79e6\u4e0d\u53d7\u8d4f\uff0c\u51fb\u664b\u5b81\u4e3a\u529f\u3002\u8131\u8eab\u767d\u5203\u91cc\uff0c\u6740\u4eba\u7ea2\u5c18\u4e2d\u3002\u5f53\u671d\u63d6\u9ad8\u4e49\uff0c\u4e3e\u4e16\u79f0\u82f1\u96c4\u3002\u5c0f\u8282\u5c82\u8db3\u8a00\uff0c\u9000\u8015\u8202\u9675\u4e1c\u3002\u5f52\u6765\u65e0\u4ea7\u4e1a\uff0c\u751f\u4e8b\u5982\u8f6c\u84ec\u3002\u4e00\u671d\u4e4c\u88d8\u655d\uff0c\u767e\u9552\u9ec4\u91d1\u7a7a\u3002\u5f39\u5251\u5f92\u6fc0\u6602\uff0c\u51fa\u95e8\u60b2\u8def\u7a77\u3002\u543e\u5144\u9752\u4e91\u58eb\n\u5929\u53f0\u90bb\u56db\u660e\uff0c\u534e\u9876\u9ad8\u767e\u8d8a\u3002\u95e8\u6807\u8d64\u57ce\u971e\uff0c\u697c\u6816\u6ca7\u5c9b\u6708\u3002\u51ed\u9ad8\u767b\u8fdc\u89c8\uff0c\u76f4\u4e0b\u89c1\u6e9f\u6e24\u3002\u4e91\u5782\u5927\u9e4f\u7ffb\uff0c\u6ce2\u52a8\u5de8\u9ccc\u6ca1\u3002\u98ce\u6f6e\u4e89\u6c79\u6d8c\uff0c\u795e\u602a\u4f55\u7fd5\u5ffd\u3002\u89c2\u5947\u8ff9\u65e0\u502a\uff0c\u597d\u9053\u5fc3\u4e0d\u6b47\u3002\u6500\u6761\u6458\u6731\u5b9e\uff0c\u670d\u836f\u70bc\u91d1\u9aa8\u3002\u5b89\u5f97\u751f\u7fbd\u6bdb\uff0c\u5343\u6625\u5367\u84ec\u9619\uff1f\n\u6728\u53f6\u7eb7\u7eb7\u4e0b\uff0c\u4e1c\u5357\u65e5\u70df\u971c\u3002\u6797\u5c71\u76f8\u665a\u66ae\uff0c\u5929\u6d77\u7a7a\u9752\u82cd\u3002 \u669d\u8272\u51b5\u590d\u4e45\uff0c\u79cb\u58f0\u4ea6\u4f55\u957f\u3002\u5b64\u821f\u517c\u5fae\u6708\uff0c\u72ec\u591c\u4ecd\u8d8a\u4e61\u3002 \u5bd2\u7b1b\u5bf9\u4eac\u53e3\uff0c\u6545\u4eba\u5728\u8944\u9633\u3002\u548f\u601d\u52b3\u4eca\u5915\uff0c\u6c5f\u6c49\u9065\u76f8\u671b\u3002\n\u6545\u5173\u8870\u8349\u904d\uff0c\u79bb\u522b\u81ea\u582a\u60b2\u3002\u8def\u51fa\u5bd2\u4e91\u5916\uff0c\u4eba\u5f52\u66ae\u96ea\u65f6\u3002 \u5c11\u5b64\u4e3a\u5ba2\u65e9\uff0c\u591a\u96be\u8bc6\u541b\u8fdf\u3002\u63a9\u6cea\u7a7a\u76f8\u5411\uff0c\u98ce\u5c18\u4f55\u5904\u671f\u3002\n\u50fb\u5df7\u90bb\u5bb6\u5c11\uff0c\u8305\u6a90\u559c\u5e76\u5c45\u3002\u84b8\u68a8\u5e38\u5171\u7076\uff0c\u6d47\u85a4\u4ea6\u540c\u6e20\u3002 \u4f20\u5c50\u671d\u5bfb\u836f\uff0c\u5206\u706f\u591c\u8bfb\u4e66\u3002\u867d\u7136\u5728\u57ce\u5e02\uff0c\u8fd8\u5f97\u4f3c\u6a35\u6e14\u3002\n\u6b8b\u706f\u65e0\u7130\u5f71\u5e62\u5e62\uff0c\u6b64\u5915\u95fb\u541b\u8c2a\u4e5d\u6c5f\u3002 \u5782\u6b7b\u75c5\u4e2d\u60ca\u5750\u8d77\uff0c\u6697\u98ce\u5439\u96e8\u5165\u5bd2\u7a97\u3002\n\u9aa8\u8089\u7f18\u679d\u53f6\uff0c\u7ed3\u4ea4\u4ea6\u76f8\u56e0\u3002\u56db\u6d77\u7686\u5144\u5f1f\uff0c\u8c01\u4e3a\u884c\u8def\u4eba\u3002\u51b5\u6211\u8fde\u679d\u6811\uff0c\u4e0e\u5b50\u540c\u4e00\u8eab\u3002\u6614\u4e3a\u9e33\u4e0e\u9e2f\uff0c\u4eca\u4e3a\u53c2\u4e0e\u8fb0\u3002\u6614\u8005\u5e38\u76f8\u8fd1\uff0c\u9088\u82e5\u80e1\u4e0e\u79e6\u3002\u60df\u5ff5\u5f53\u79bb\u522b\uff0c\u6069\u60c5\u65e5\u4ee5\u65b0\u3002\u9e7f\u9e23\u601d\u91ce\u8349\uff0c\u53ef\u4ee5\u55bb\u5609\u5bbe\u3002\u6211\u6709\u4e00\u7f47\u9152\uff0c\u6b32\u4ee5\u8d60\u8fdc\u4eba\u3002\u613f\u5b50\u7559\u659f\u914c\n\u671d\u95fb\u6e38\u5b50\u5531\u79bb\u6b4c\uff0c\u6628\u591c\u5fae\u971c\u521d\u6e21\u6cb3\u3002\u9e3f\u96c1\u4e0d\u582a\u6101\u91cc\u542c\uff0c\u4e91\u5c71\u51b5\u662f\u5ba2\u4e2d\u8fc7\u3002\u5173\u57ce\u6811\u8272\u50ac\u5bd2\u8fd1\uff0c\u5fa1\u82d1\u7827\u58f0\u5411\u665a\u591a\u3002\u83ab\u89c1\u957f\u5b89\u884c\u4e50\u5904\uff0c\u7a7a\u4ee4\u5c81\u6708\u6613\u8e49\u8dce\u3002\n\u9676\u516c\u6709\u9038\u5174\uff0c\u4e0d\u4e0e\u5e38\u4eba\u4ff1\u3002\u7b51\u53f0\u50cf\u534a\u6708\uff0c\u56de\u5411\u9ad8\u57ce\u9685\u3002\u7f6e\u9152\u671b\u767d\u4e91\uff0c\u5546\u98d9\u8d77\u5bd2\u68a7\u3002\u79cb\u5c71\u5165\u8fdc\u6d77\uff0c\u6851\u67d8\u7f57\u5e73\u829c\u3002\u6c34\u8272\u6e0c\u4e14\u660e\uff0c\u4ee4\u4eba\u601d\u955c\u6e56\u3002\u7ec8\u5f53\u8fc7\u6c5f\u53bb\uff0c\u7231\u6b64\u6682\u8e1f\u8e70\u3002\n\u7eff\u6c34\u63a5\u67f4\u95e8\uff0c\u6709\u5982\u6843\u82b1\u6e90\u3002\u5fd8\u5fe7\u6216\u5047\u8349\uff0c\u6ee1\u9662\u7f57\u4e1b\u8431\u3002\u669d\u8272\u6e56\u4e0a\u6765\uff0c\u5fae\u96e8\u98de\u5357\u8f69\u3002\u6545\u4eba\u5bbf\u8305\u5b87\uff0c\u5915\u9e1f\u6816\u6768\u56ed\u3002\u8fd8\u60dc\u8bd7\u9152\u522b\uff0c\u6df1\u4e3a\u6c5f\u6d77\u8a00\u3002\u660e\u671d\u5e7f\u9675\u9053\uff0c\u72ec\u5fc6\u6b64\u503e\u6a3d\u3002\n\u5c71\u9ad8\uff0c\u8d8a\u6c34\u6e05\uff0c\u63e1\u624b\u65e0\u8a00\u4f24\u522b\u60c5\u3002\u5c06\u6b32\u8f9e\u541b\u6302\u5e06\u53bb\uff0c\u79bb\u9b42\u4e0d\u6563\u70df\u90ca\u6811\u3002\u6b64\u5fc3\u90c1\u6005\u8c01\u80fd\u8bba\uff0c\u6709\u6127\u53e8\u627f\u56fd\u58eb\u6069\u3002\u4e91\u7269\u5171\u503e\u4e09\u6708\u9152\uff0c\u5c81\u65f6\u540c\u996f\u4e94\u4faf\u95e8\u3002\u7fa1\u541b\u7d20\u4e66\u5c1d\u6ee1\u6848\uff0c\u542b\u4e39\u7167\u767d\u971e\u8272\u70c2\u3002\u4f59\u5c1d\u5b66\u9053\u7a77\u51a5\u7b4c\uff0c\u68a6\u4e2d\u5f80\u5f80\u6e38\u4ed9\u5c71\u3002\u4f55\u5f53\u8131\u5c63\u8c22\u65f6\n\u4fa7\u53e0\u4e07\u53e4\u77f3\uff0c\u6a2a\u4e3a\u767d\u9a6c\u77f6\u3002\u4e71\u6d41\u82e5\u7535\u8f6c\uff0c\u4e3e\u6389\u626c\u73e0\u8f89\u3002\u4e34\u9a7f\u5377\u7f07\u5e55\uff0c\u5347\u5802\u63a5\u7ee3\u8863\u3002\u60c5\u4eb2\u4e0d\u907f\u9a6c\uff0c\u4e3a\u6211\u89e3\u971c\u5a01\u3002\n\u8c2a\u53bb\u541b\u65e0\u6068\uff0c\u95fd\u4e2d\u6211\u65e7\u8fc7\u3002\u5927\u90fd\u79cb\u96c1\u5c11\uff0c\u53ea\u662f\u591c\u733f\u591a\u3002 \u4e1c\u8def\u4e91\u5c71\u5408\uff0c\u5357\u5929\u7634\u75a0\u548c\u3002\u81ea\u5f53\u9022\u96e8\u9732\uff0c\u884c\u77e3\u614e\u98ce\u6ce2\u3002\n\u5ce5\u5d58\u8d64\u4e91\u897f\uff0c\u65e5\u811a\u4e0b\u5e73\u5730\u3002\u67f4\u95e8\u9e1f\u96c0\u566a\uff0c\u5f52\u5ba2\u5343\u91cc\u81f3\u3002 \u59bb\u5b65\u602a\u6211\u5728\uff0c\u60ca\u5b9a\u8fd8\u62ed\u6cea\u3002\u4e16\u4e71\u906d\u98d8\u8361\uff0c\u751f\u8fd8\u5076\u7136\u9042\u3002 \u90bb\u4eba\u6ee1\u5899\u5934\uff0c\u611f\u53f9\u4ea6\u6b54\u6b37\u3002\u591c\u9611\u66f4\u79c9\u70db\uff0c\u76f8\u5bf9\u5982\u68a6\u5bd0\u3002 \u665a\u5c81\u8feb\u5077\u751f\uff0c\u8fd8\u5bb6\u5c11\u6b22\u8da3\u3002\u5a07\u513f\u4e0d\u79bb\u819d\uff0c\u754f\u6211\u590d\u5374\u53bb\u3002 \u5fc6\u6614\u597d\u8ffd\n\u8352\u620d\u843d\u9ec4\u53f6\uff0c\u6d69\u7136\u79bb\u6545\u5173\u3002\u9ad8\u98ce\u6c49\u9633\u6e21\uff0c\u521d\u65e5\u90e2\u95e8\u5c71\u3002 \u6c5f\u4e0a\u51e0\u4eba\u5728\uff0c\u5929\u6daf\u5b64\u68f9\u8fd8\u3002\u4f55\u5f53\u91cd\u76f8\u89c1\uff0c\u5c0a\u9152\u6170\u79bb\u989c\u3002\n\u6298\u82b1\u9022\u9a7f\u4f7f\uff0c\u5bc4\u4e0e\u9647\u5934\u4eba\u3002\u6c5f\u5357\u65e0\u6240\u6709\uff0c\u804a\u8d60\u4e00\u679d\u6625\u3002\n\u5df2\u8fc7\u624d\u8ffd\u95ee\uff0c\u76f8\u770b\u662f\u6545\u4eba\u3002\u4e71\u79bb\u4f55\u5904\u89c1\uff0c\u6d88\u606f\u82e6\u96be\u771f\u3002\u62ed\u773c\u60ca\u9b42\u5b9a\uff0c\u8854\u676f\u7b11\u8bed\u9891\u3002\u79fb\u5bb6\u5c31\u543e\u4f4f\uff0c\u767d\u9996\u4e24\u9057\u6c11\u3002\n\u8352\u90ae\u53e4\u620d\uff0c\u5269\u6570\u6735\u5b64\u82b1\uff0c\u843d\u82f1\u5982\u8bb8\u3002\u91c7\u9999\u4eba\u53bb\u3002\u95ee\u659c\u9633\u4e00\u62b9\uff0c\u5e7d\u60c5\u8c01\u8bc9\u3002\u91d1\u7c89\u51c4\u8ff7\uff0c\u4ed8\u4e0e\u4e8c\u5206\u5c18\u571f\u3002\u65e0\u60c5\u7eea\u3002\u4f24\u6ca6\u843d\u5929\u6daf\uff0c\u98d8\u96f6\u4f3c\u6c5d\u3002\u9605\u4e1c\u98ce\u51e0\u5ea6\u3002\u770b\u4e07\u70b9\u82b1\u98de\uff0c\u6625\u5149\u53c8\u66ae\u3002\u82b3\u5fc3\u81ea\u82e6\u3002\u7389\u989c\u6194\u60b4\uff0c\u7476\u534e\u65e0\u8bed\u3002\u4e00\u7b11\u5ae3\u7136\uff0c\u80af\u5b66\u592d\n\u6211\u5c45\u5317\u6d77\u541b\u5357\u6d77\uff0c\u4e07\u91cc\u4f55\u66fe\u9694\u4e00\u5c18\u3002\u5165\u8700\u8bd7\u7bc7\u5fe7\u56fd\u6cea\uff0c\u6821\u4e66\u751f\u6b7b\u6545\u4eba\u5fc3\u3002\u4e0d\u671f\u5929\u5916\u4e2d\u79cb\u8282\uff0c\u76f8\u89c1\u697c\u9ad8\u4e03\u5341\u5c42\u3002\u5e7f\u5b87\u96c4\u90fd\u51ed\u4fef\u4ef0\uff0c\u708e\u9ec4\u5e94\u5e86\u6709\u513f\u5b59\u3002\n\u611f\u541b\u76f8\u8d60\u610f\uff0c\u7518\u99a8\u6ee1\u5e8a\u524d\u3002\u800c\u6211\u5ffd\u4e0d\u4e50\uff0c\u601d\u5f7c\u82f1\u683c\u5170\u3002\u5982\u4f55\u4e3a\u6b64\u82b1\uff0c\u6d41\u8840\u4e00\u767e\u5e74\u3002\n\u6d77\u5185\u5b58\u77e5\u5df1\uff0c\u5929\u6daf\u82e5\u6bd4\u90bb\u3002\u53e4\u4eba\u804a\u81ea\u9063\uff0c\u6b64\u8bed\u603b\u975e\u771f\u3002\u5b66\u95ee\u591a\u5947\u5b57\uff0c\u89c2\u4e66\u5c11\u65ab\u8f6e\u3002\u4f55\u65f6\u7b56\u6756\u5c66\uff0c\u5171\u9189\u79e3\u9675\u6625\uff1f\n\u6614\u540c\u767d\u65e5\u5347\u7a79\u78a7\uff0c\u8fd8\u540c\u6c34\u56fd\u53f8\u6c11\u7c4d\u3002\u4e09\u5e74\u4e0d\u89c1\u9891\u5bc4\u4e66\uff0c\u4e24\u6708\u76f8\u8fc7\u5fd8\u884c\u5f79\u3002\u6843\u82b1\u6c5f\u4e0a\u96ea\u970f\u970f\u3002\u9ec4\u9e64\u697c\u4e2d\u98ce\u529b\u5fae\u3002\u5e7d\u80dc\u903c\u4eba\u9b42\u6b32\u98de\uff0c\u6b64\u65f6\u4e0d\u9189\u65e0\u4e0e\u5f52\u3002\u4eba\u95f4\u7f8e\u4e8b\u5e94\u6709\u4e3b\uff0c\u522b\u9a6c\u8e44\u6cbe\u8fb9\u585e\u571f\u3002\u5357\u697c\u4e00\u671b\u5fc3\u51c4\u8ff7\uff0c\u6628\u591c\u758f\u7c37\n\u67f3\u6761\u98ce\u6696\u4f1a\u541f\u65f6\uff0c\u6797\u4e0b\u6c60\u8fb9\u5c50\u9f7f\u79fb\u3002\xa0\u522b\u540e\u8fc7\u4ece\u66f4\u758f\u61d2\uff0c\u66ae\u8749\u5639\u4e71\u4e0d\u80dc\u60b2\u3002\n\u4eba\u751f\u4e09\u4e07\u516d\u5343\u65e5\uff0c\u4e8c\u4e07\u65e5\u4e2d\u6101\u82e6\u8eab\u3002\u60df\u6709\u65e0\u5fc3\u6d88\u9063\u5f97\uff0c\u6709\u5fc3\u5230\u4e86\u662f\u75f4\u4eba\u3002\n\u6768\u82b1\u96f6\u843d\u66ae\u6625\u98ce\uff0c\u9189\u8d77\u5357\u8f69\u5915\u7167\u7ea2\u3002\u9592\u501a\u7126\u6850\u5750\u65e0\u8bed\uff0c\u6545\u4eba\u76f8\u9694\u6d77\u95e8\u4e1c\u3002\n\u751f\u5fc6\u7720\u4e91\u5904\uff0c\u5f53\u6a90\u5217\u7fe0\u5c4f\u3002\u6709\u65f6\u804a\u6781\u76ee\uff0c\u5c3d\u65e5\u5750\u5fd8\u5f62\u3002\u53bb\u6b64\u5fc3\u60df\u82e6\uff0c\u60ac\u56fe\u6068\u5ffd\u9192\u3002\u6570\u5cf0\u79cb\u6b32\u6d3b\uff0c\u865a\u7c41\u591c\u91cd\u542c\u3002\u5730\u9547\u4e09\u5434\u8fdc\uff0c\u5929\u8fde\u4e07\u53e4\u9752\u3002\u51e0\u4eba\u66fe\u5f97\u9053\uff0c\u662f\u8349\u5373\u901a\u7075\u3002\u677e\u9e64\u9592\u7aa5\u9f0e\uff0c\u9f9b\u50e7\u95c7\u8bf5\u7ecf\u3002\u7ec8\u987b\u535c\u957f\u5f80\uff0c\u56de\n\u8042\u5c27\u6000\u62b1\u6240\u7fd8\u7136\uff0c\u6e38\u5fc3\u5723\u9053\u4e09\u5341\u5e74\u3002\u667a\u6cc9\u6e4d\u6fc0\u6a2a\u78a7\u6d77\uff0c\u6587\u5c71\u5947\u5d1b\u51cc\u9752\u5929\u3002\u795e\u5145\u6c14\u6ea2\u7738\u5b50\u597d\uff0c\u5e38\u8c08\u5c3d\u662f\u7ecf\u7eb6\u9053\u3002\u65e9\u6c42\u516b\u7528\u6d3b\u82cd\u9ed4\uff0c\u83ab\u5411\u5c18\u4e2d\u865a\u81ea\u8001\u3002\n\u5d69\u9633\u5cf0\u5e95\u6d1e\u4e2d\u5929\uff0c\u66fe\u5171\u6d6e\u4e18\u5bf9\u63a9\u5173\u3002\u77e5\u9053\u9ad8\u9592\u5c11\u517c\u6d4e\uff0c\u6298\u8170\u4ece\u6b64\u5230\u4eba\u95f4\u3002\n\u9592\u6101\u4f11\u95ee\u610f\u4f11\u7591\uff0c\u6b64\u4e8b\u5173\u8eab\u5408\u518d\u601d\u3002\u5fc3\u9759\u6613\u6c42\u957f\u4e16\u6cd5\uff0c\u6c14\u72c2\u96be\u4e0e\u5c11\u5e74\u65f6\u3002\u65b0\u7f16\u5230\u5e95\u5c06\u4f55\u7528\uff0c\u65e7\u597d\u5982\u4eca\u66f4\u6709\u8c01\u3002\u72b9\u5fc6\u516c\u5802\u79cb\u4f1a\u5426\uff0c\u7ed5\u7a97\u5bd2\u7af9\u591c\u98ce\u5439\u3002\n\u8001\u53bb\u6175\u4ecd\u751a\uff0c\u8363\u540d\u65e7\u65e5\u758f\u3002\u53c8\u56e0\u7ecf\u5c81\u75c5\uff0c\u4e0d\u7b54\u6545\u4eba\u4e66\u3002\u95ed\u95a4\u50e7\u5f52\u6263\uff0c\u770b\u5c71\u540f\u6563\u521d\u3002\u4f55\u56e0\u95ee\u5fc3\u8ff9\uff0c\u4e4b\u5b50\u6700\u76f8\u65bc\u3002\n\u758f\u758f\u82a6\u82c7\u6620\u95e8\u5899\uff0c\u66f4\u6709\u65b0\u79cb\u810d\u5473\u957f\u3002\u4f55\u4e8b\u8f7b\u629b\u6765\u5e1d\u91cc\uff0c\u81f3\u4eca\u9b42\u68a6\u7ed5\u5bd2\u5858\u3002\n\u82f1\u8d24\u53bb\u4e16\u4e16\u540c\u60b2\uff0c\u7ba1\u845b\u624d\u80fd\u66f4\u6bd4\u8c01\u3002\u5fc3\u5c3d\u897f\u5c71\u79bb\u9690\u540e\uff0c\u540d\u55a7\u6df1\u6bbf\u53d7\u6069\u65f6\u3002\u8c0b\u7337\u4e0d\u6789\u79f0\u4eba\u6770\uff0c\u7cbe\u5ba1\u771f\u582a\u4f5c\u540f\u5e08\u3002\u6f2b\u5c31\u9ad8\u539f\u5411\u4e1c\u54ed\uff0c\u8def\u957f\u5929\u8fdc\u5c82\u80fd\u77e5\u3002\n\u5e1d\u4e61\u4e09\u5341\u5e74\u524d\u522b\uff0c\u6c5f\u5916\u76f8\u9022\u9b13\u5df2\u8870\u3002\u6e05\u8bba\u672a\u7a77\u884c\u8ba1\u901f\u3002\u4e3a\u541b\u4e34\u6c34\u7acb\u591a\u65f6\u3002\n\u95fb\u8bf4\u6000\u7ecf\u6d4e\uff0c\u4e58\u9592\u8fc7\u9690\u5e90\u3002\u8bdd\u8a00\u6b8a\u4e0d\u9006\uff0c\u4ea4\u5206\u5408\u4f55\u5982\u3002\u9053\u6127\u5c06\u65f6\u80cc\uff0c\u5fc3\u975e\u4e0e\u4e16\u758f\u3002\u51c4\u51c4\u91cd\u60dc\u522b\uff0c\u8749\u566a\u53e4\u69d0\u521d\u3002\n\xa0 \u7edb\u6c14\u6d6e\u829d\u5b87\uff0c\u82b3\u98ce\u88ad\u82b0\u8863\u3002\u4ed9\u6e38\u4f20\u51e4\u5439\uff0c\u9f99\u5367\u51c0\u4e91\u970f\u3002\u7247\u53f6\u6d6e\u971c\u5c0f\uff0c\u4e38\u9e3f\u8d34\u6c49\u5fae\u3002\u6b64\u65f6\u77bb\u773a\u8fdc\uff0c\u76f4\u6b32\u63fd\u652f\u673a\u3002\u84dd\u82e5\u5ef6\u4f73\u8d4f\uff0c\u8559\u80b4\u7559\u597d\u5bbe\u3002\u5171\u542c\u8054\u8882\u66f2\uff0c\u4e0d\u89c1\u6298\u9999\u4eba\u3002\u50e7\u626b\u82d4\u95f4\u5750\uff0c\u4e91\u751f\u5934\u4e0a\u5dfe\u3002\u77f3\u5e8a\u6d51\n\u67ab\u843d\u7a7a\u6c5f\u751f\u51bb\u70df\uff0c\u897f\u98ce\u7fb8\u9a6c\u4e0d\u80dc\u97ad\u3002\u51b0\u6d88\u6d59\u6c34\u77e5\u5bb6\u8fd1\uff0c\u6625\u5230\u95fd\u5c71\u5728\u5ba2\u5148\u3002\u659c\u65e5\u96c1\u8fb9\u770b\u6545\u56fd\uff0c\u5b64\u5e06\u96ea\u91cc\u8fc7\u6b8b\u5e74\u3002\u601c\u4e88\u4e45\u8d1f\u5bd2\u9e25\u7ea6\uff0c\u9b42\u68a6\u4ece\u541b\u78a7\u6d77\u5929\u3002"})}function u(n={}){const{wrapper:e}={...(0,s.R)(),...n.components};return e?(0,i.jsx)(e,{...n,children:(0,i.jsx)(p,{...n})}):p(n)}},28453:(n,e,t)=>{t.d(e,{R:()=>o,x:()=>a});var r=t(96540);const i={},s=r.createContext(i);function o(n){const e=r.useContext(s);return r.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(i):n.components||i:o(n.components),r.createElement(s.Provider,{value:e},n.children)}}}]);